import React, { useState } from "react";
import axios from "axios";
import ButtonLink from "components/ButtonLink/Index";

export default function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      // const response = await fetch('url', 'POST', {name, email, password});
      const response = await axios.post("http://localhost:1234", {
        username,
        password,
      });
      const data = JSON.parse(response.data);
      // console.log(`Nombre almacenado: ${username}`);
      // console.log(`Password almacenado: ${password}`);
    } catch (exception) {
      setError(exception.message);
    }
  }

  function handleChangeUserame(event) {
    setUsername(event.target.value);
  }

  function handleChangePassword(event) {
    setPassword(event.target.value);
  }

  return (
    <>
      <LogoMedium />

      <div>
        <form onSubmit={handleSubmit}>
          {error.length > 0 && <div>Error: {error}</div>}
          <div>
            <label>nombre</label>
            <input type="text" onChange={handleChangeUserame} />
          </div>
          <div>
            <label>contrase√±a</label>
            <input type="password" onChange={handleChangePassword} />
          </div>
        </form>

        <ButtonLink type="submit" whereTo="/register" className="button">
          GO!
        </ButtonLink>
      </div>
    </>
  );
}
